/*
 *功能：基频带通滤波器
 *y[n] = 0.78795*(x[n] - x[n-2]) + 1.72289*y[n-1] - 0.75815*y[n-2]
 *z[n] = 0.78795*(y[n] - y[n-2]) - 1.34324*z[n-1] - 0.51579*z[n-2]
 */
void AF_Filter(float* lfBufOut,float* afBufOut){
	float y=0;
	int i=0;
	for(i=0;i<SEND_SIZE;i++){
		//第一组二阶子系统计算
		reg0[2]=lfBufOut[i]-a0[1]*reg0[1]-a0[2]*reg0[0];//	0.78795*(x[n] - x[n-2]) + 1.72289*y[n-1] - 0.75815*y[n-2]
		y=b0[0]*reg0[2]+b0[1]*reg0[1]+b0[2]*reg0[0];    //	0.78795*(y[n] - y[n-2])
		reg0[0]=reg0[1];
		reg0[1]=reg0[2];

		//第二组二阶子系统计算
		reg1[2]=y-a1[1]*reg1[1]-a1[2]*reg1[0];
		afBufOut[i]=b1[0]*reg1[2]+b1[1]*reg1[1]+b1[2]*reg1[0];
		reg1[0]=reg1[1];
		reg1[1]=reg1[2];
	}
}

